// src/services/analytics.js
const API = process.env.REACT_APP_API_URL || "http://localhost:3002/api";

function authHeaders() {
  const token = localStorage.getItem("token") || "";
  return token ? { Authorization: `Bearer ${token}` } : {};
}

export async function getRevenueLast6Months() {
  const res = await fetch(`${API}/private/analytics/revenue-6m`, {
    headers: { "Content-Type": "application/json", ...authHeaders() },
  });
  if (!res.ok) throw new Error("Failed to load revenue data");
  // -> [{ month: "Jan", revenue: 123.45 }, ...]  (6 points)
  return res.json();
}

export async function getWeeklyEngagement() {
  const res = await fetch(`${API}/private/analytics/weekly-engagement`, {
    headers: { "Content-Type": "application/json", ...authHeaders() },
  });
  if (!res.ok) throw new Error("Failed to load engagement data");
  // -> [{ day:"Mon", likes:10, comments:3, shares:0 }, ...] (7 points)
  return res.json();
}
