import React, { useEffect, useState } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { getCreatorPosts } from '../services/posts.js'; // <-- new service

const CreatorProfile = ({ isDarkMode }) => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [creator, setCreator] = useState(null);
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);       // public profile loading
  const [postsLoading, setPostsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isSubscribed, setIsSubscribed] = useState(false);

  // 1) public profile
  useEffect(() => {
    let alive = true;
    setLoading(true);
    fetch(`http://localhost:3002/api/public/creator/${id}`)
      .then(res => {
        if (!res.ok) throw new Error('Failed to fetch creator');
        return res.json();
      })
      .then(data => { if (alive) { setCreator(data); setLoading(false); } })
      .catch(err => { if (alive) { setError(err.message); setLoading(false); } });
    return () => { alive = false; };
  }, [id]);

  // 2) private posts (gated by subscription)
  useEffect(() => {
    let alive = true;
    setPostsLoading(true);

    (async () => {
      try {
        const res = await getCreatorPosts(id); // returns json OR Response depending on your service

        // If you used Option A (makeRequest returning data on OK and the raw Response on !ok)
        if (res?.status === 401) {
          if (alive) { setIsSubscribed(false); setPosts([]); }
          return;
        }
        if (Array.isArray(res)) {
          if (alive) { setIsSubscribed(true); setPosts(res); }
          return;
        }

        // If you used Option B (plain fetch returning Response)
        if (res instanceof Response) {
          if (res.status === 401) {
            if (alive) { setIsSubscribed(false); setPosts([]); }
          } else if (res.ok) {
            const data = await res.json();
            if (alive) { setIsSubscribed(true); setPosts(Array.isArray(data) ? data : data.posts || []); }
          } else {
            if (alive) { setIsSubscribed(false); setPosts([]); }
          }
        }
      } catch {
        if (alive) { setIsSubscribed(false); setPosts([]); }
      } finally {
        if (alive) setPostsLoading(false);
      }
    })();

    return () => { alive = false; };
  }, [id]);

  if (loading) {
    return (
      <div className={`min-h-screen flex items-center justify-center ${isDarkMode ? 'bg-gray-900 text-white' : 'bg-white text-gray-900'}`}>
        Loading creator...
      </div>
    );
  }

  if (error || !creator) {
    return (
      <div className={`min-h-screen flex items-center justify-center ${isDarkMode ? 'bg-gray-900 text-white' : 'bg-white text-gray-900'}`}>
        <div className="text-center">
          <h2 className="text-2xl font-bold mb-4">Creator not found</h2>
          <Link to="/" className="text-blue-500 underline">Back to Home</Link>
        </div>
      </div>
    );
  }

  const handleSubscribe = () => {
    // e.g., navigate to your subscribe flow
    navigate(`/subscribe/${id}`);
  };

  return (
    <div className={`min-h-screen transition-colors duration-300 ${isDarkMode ? 'bg-gray-900 text-white' : 'bg-white text-gray-900'}`}>  
      <div className="max-w-3xl mx-auto py-16 px-4">
        <Link to="/" className="text-blue-500 underline mb-8 inline-block">← Back to Home</Link>

        <div className="flex flex-col items-center mb-8">
          <img
            src={creator.profilePic?.url || '/images/creator.jpg'}
            alt={creator.username || creator.name}
            className="w-32 h-32 rounded-full mb-4 object-cover"
          />
          <h1 className="text-4xl font-bold mb-2">{creator.username || creator.name}</h1>
          <p className="text-lg text-center mb-4">{creator.bio || creator.description}</p>
        </div>

        <div>
          <h2 className="text-2xl font-semibold mb-4">Posts</h2>

          {postsLoading ? (
            <div className="text-center opacity-70">Loading posts…</div>
          ) : isSubscribed ? (
            posts.length ? (
              <div className="space-y-6">
                {posts.map(post => (
                  <div
                    key={post._id || post.id}
                    className={`p-6 rounded-xl shadow border ${isDarkMode ? 'bg-gray-800 border-gray-700' : 'bg-white border-gray-200'}`}
                  >
                    <h3 className="text-xl font-bold mb-2">{post.title}</h3>
                    <p className="whitespace-pre-wrap">{post.content || post.body}</p>

                    {/* attachments (optional) */}
                    {Array.isArray(post.attachments) && post.attachments.length > 0 && (
                      <div className="mt-4 grid grid-cols-1 gap-3">
                        {post.attachments.map((att, i) => (
                          <a
                            href={att.url}
                            target="_blank"
                            rel="noreferrer"
                            key={att.publicId || i}
                            className="underline break-all"
                          >
                            Attachment {i + 1}
                          </a>
                        ))}
                      </div>
                    )}
                  </div>
                ))}
              </div>
            ) : (
              <div className="text-center opacity-70">No posts yet.</div>
            )
          ) : (
            <div className="text-center p-8 border rounded-xl bg-yellow-100 text-yellow-800">
              <p>You must subscribe to view this creator's posts.</p>
              <button
                onClick={handleSubscribe}
                className="mt-4 px-6 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition"
              >
                Subscribe
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default CreatorProfile;
