import React, { useEffect, useState, useRef } from "react";
import { useParams, useLocation } from "react-router-dom";
import { getPost } from "../services/posts"; // ✅ update this if your function is elsewhere
import CommentList from "./CommentList"; // your existing comment UI component
import Navbar from "./Navbar";
import Footer from "./Footer";

const PostPage = ({ isDarkMode, toggleTheme, isLoggedIn, logout }) => {
  const { id: postId } = useParams(); // ✅ alias `id` to `postId`
  const location = useLocation();
  const [post, setPost] = useState(null);
  const [highlightedCommentId, setHighlightedCommentId] = useState(null);

  useEffect(() => {
    const searchParams = new URLSearchParams(location.search);
    const highlight = searchParams.get("highlight");
    if (highlight) {
      setHighlightedCommentId(highlight);
    }
  }, [location.search]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const data = await getPost(postId);
        setPost(data);
      } catch (err) {
        console.error("Error loading post:", err);
      }
    };
    fetchData();
  }, [postId]);

  if (!post) return <div className="text-center mt-10">Loading post...</div>;

  return (
    <div className={`min-h-screen ${isDarkMode ? "bg-gray-900 text-white" : "bg-white text-black"}`}>
      <Navbar isDarkMode={isDarkMode} toggleTheme={toggleTheme} isLoggedIn={isLoggedIn} logout={logout} />
      <main className="max-w-2xl mx-auto py-12 px-4">
        <h1 className="text-3xl font-bold mb-4">{post.title}</h1>
        <p className="text-muted-foreground mb-6">{post.body}</p>

        <hr className="my-6" />
        <h2 className="text-2xl font-semibold mb-4">Comments</h2>
        <CommentList comments={post.comments} highlightId={highlightedCommentId} />
      </main>
      <Footer isDarkMode={isDarkMode} />
    </div>
  );
};

export default PostPage;
