import React, { useEffect, useState } from "react";
import { useLocation, useSearchParams } from "react-router-dom";
import { usePost } from "../contexts/PostContext";
import CommentsSection from "./CommentsSection";
import Navbar from "./Navbar";
import Footer from "./Footer";
import PostCard from "./PostCard"; // ← add

const PostPage = ({ isDarkMode, toggleTheme, isLoggedIn, logout }) => {
  const location = useLocation();
  const [search] = useSearchParams();
  const [highlightedCommentId, setHighlightedCommentId] = useState(null);

  const {
    post,
    postId,
    comments,
    getReplies,
    createComment,
    updateComment,
    deleteComment,
    toggleCommentLike,
    currentUser,
    loading,
  } = usePost();

  // Read highlight target from state (preferred) or query (?comment= / ?highlight=)
  useEffect(() => {
    const fromState = location.state?.highlightCommentId || null;
    const fromQuery = search.get("comment") || search.get("highlight") || null;
    setHighlightedCommentId(fromState || fromQuery);
  }, [location.state, search]);

  // Optional: smooth-scroll to the highlighted comment when comments load
  useEffect(() => {
    if (!highlightedCommentId || !comments?.length) return;
    // Try two common hooks the comment item might expose:
    const el =
      document.querySelector(`[data-comment-id="${highlightedCommentId}"]`) ||
      document.getElementById(`comment-${highlightedCommentId}`);
    if (el) {
      el.scrollIntoView({ behavior: "smooth", block: "center" });
      el.classList.add("ring-2", "ring-blue-500");
      setTimeout(() => el.classList.remove("ring-2", "ring-blue-500"), 1500);
    }
  }, [highlightedCommentId, comments]);

   // ---- NEW: build an author fallback from whatever the post contains
  const author = useMemo(() => {
    const a = (post?.author && typeof post.author === "object")
      ? post.author
      : (post?.creator && typeof post.creator === "object")
      ? post.creator
      : null;

    if (a) return {
      _id: a._id || a.id,
      username: a.username || a.name,
      handle: a.handle,
      profilePic: a.profilePic || a.avatar,
    };

    // last resort: infer from flat fields if your API returns them
    return {
      _id: post?.authorId || post?.creatorId,
      username: post?.authorName || post?.creatorName,
      profilePic: post?.authorPic ? { url: post.authorPic } : undefined,
    };
  }, [post]);

  if (loading) return <div className="text-center mt-10">Loading post...</div>;
  if (!post)   return <div className="text-center mt-10">Post not found</div>;

  return (
    <div className={`min-h-screen ${isDarkMode ? "bg-gray-900 text-white" : "bg-white text-black"}`}>
      <Navbar isDarkMode={isDarkMode} toggleTheme={toggleTheme} isLoggedIn={isLoggedIn} logout={logout} />
      <main className="max-w-2xl mx-auto py-12 px-4">
        <PostCard
  post={post}
  isDarkMode={isDarkMode}
   author={author}                 // ← pass the fallback author
  onLikeToggle={() => {}}
  highlightId={highlightedCommentId}
>
  {/* If you’re keeping CommentsSection inside the card: */}
  <CommentsSection
    postId={postId}
    title="Comments"
    highlightId={highlightedCommentId}   // ✅ here
  />
</PostCard>

      </main>
      <Footer isDarkMode={isDarkMode} />
    </div>
  );
};

export default PostPage;
