// at top of CreatorDashboard.jsx
import React, { useState, useEffect, useMemo } from 'react';
import { Link } from 'react-router-dom';
import { getCreatorStats, getCreatorPosts } from '../../services/dashboard';
import StatCard from './components/StatCard';
import ActivityCard from './components/ActivityCard';
import RevenueChart from './components/RevenueChart';
import EngagementChart from './components/EngagementChart';
import PostCard from './components/PostCard';
import { makeRequest } from "../../services/makeRequest";
import { getRevenueLast6Months } from '../../services/analytics';

const CreatorDashboard = ({ isDarkMode, user, dashboardData }) => {
    const [stats, setStats] = useState({
        totalSubscribers: 0,
        monthlyRevenue: 0,
        totalPosts: 0,
        engagement: 0,
        growth: '+12%'
    });
    const [posts, setPosts] = useState([]);
    const [subscribers, setSubscribers] = useState([]);
    const [revenueData, setRevenueData] = useState([]);
    const [engagementData, setEngagementData] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
  const fetchCreatorData = async () => {
    try {
      setLoading(true);

      const [subscribersData, postsData, weekly, revenueResp] = await Promise.all([
        getCreatorStats().catch(() => ({ data: [] })),
        getCreatorPosts().catch(() => ({ data: [] })),
        makeRequest("/private/analytics/weekly-engagement").catch(() => ({ data: { days: [], likesByDay: [], commentsByDay: [], sharesByDay: [] }})),
        getRevenueLast6Months().catch(() => ({ data: [] })),
      ]);

      const subs = Array.isArray(subscribersData) ? subscribersData : (subscribersData?.data || []);
      const postsArray = Array.isArray(postsData) ? postsData : (postsData?.data || []);
      const weeklyData = weekly?.data || weekly || {};
      const revenueArr = Array.isArray(revenueResp?.data) ? revenueResp.data
                       : Array.isArray(revenueResp) ? revenueResp
                       : [];

      const { days = [], likesByDay = [], commentsByDay = [], sharesByDay = [] } = weeklyData;

      setSubscribers(subs);
      setPosts(postsArray);

      // Top cards (you can refine monthlyRevenue if you want to sum revenueArr instead)
      const monthlyRevenue = subs.length * (dashboardData?.fee || 15);
      setStats({
        totalSubscribers: subs.length,
        monthlyRevenue,
        totalPosts: postsArray.length,
        engagement: Math.floor(Math.random() * 95) + 60,
        growth: subs.length > 10 ? '+12%' : '+5%',
      });

      // REAL revenue data for the chart
      // The backend already returns [{month:'Jan', revenue:Number}, ...] in order.
      setRevenueData(revenueArr);

      // REAL weekly engagement (already working)
      setEngagementData(
        days.map((day, i) => ({
          day,
          likes: likesByDay[i] ?? 0,
          comments: commentsByDay[i] ?? 0,
          shares: sharesByDay[i] ?? 0,
        }))
      );

    } catch (err) {
      console.error('Failed to fetch creator data:', err);
    } finally {
      setLoading(false);
    }
  };

  fetchCreatorData();
}, [dashboardData]);


