import StatCard from './components/StatCard';
import PlatformChart from './components/PlatformChart';
import UserTable from './components/UserTable';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../AuthContext';            // ⬅️ unified auth

const AVATAR_FALLBACK = "/images/avatar-default.png";

// Build a Cloudinary thumb if we only have a publicId
const buildCloudinary = (publicId) =>
  `https://res.cloudinary.com/db6djtrg6/image/upload/c_fill,ar=1:1,g_auto,f_auto,q_auto:good,w=80,h=80/${publicId}`;

// Ensure we return a direct IMAGE url, not a collection page
const normalizePicUrl = (p) => {
  if (!p) return AVATAR_FALLBACK;

  // cases:
  // 1) string
  if (typeof p === "string") {
    // fix collection links to proper /image/upload thumbs if needed
    if (p.includes("collection.cloudinary.com")) return AVATAR_FALLBACK;
    // optionally add a thumb transform to /image/upload/ urls
    if (p.includes("/image/upload/")) {
      return p.replace(
        "/image/upload/",
        "/image/upload/c_fill,ar=1:1,g_auto,f_auto,q_auto:good,w=80,h=80/"
      );
    }
    return p; // other providers
  }

  // 2) object { url, publicId }
  if (p.url) {
    if (p.url.includes("/image/upload/")) {
      return p.url.replace(
        "/image/upload/",
        "/image/upload/c_fill,ar=1:1,g_auto,f_auto,q_auto:good,w=80,h=80/"
      );
    }
    // non-upload url (e.g., collection link) -> fallback
    return AVATAR_FALLBACK;
  }

  if (p.publicId) return buildCloudinary(p.publicId);
  return AVATAR_FALLBACK;
};

