import React, { useState } from 'react';
import Navbar from './Navbar';
import Footer from './Footer';
import './FactsPage.css';

const FactsPage = ({ isDarkMode, toggleTheme }) => {
  const [subscribedCreators, setSubscribedCreators] = useState([]);

  const creators = [
    {
      id: 1,
      name: "Sarah Johnson",
      photo: "/images/creator-profile-1.jpg",
      specialty: "Tech & Programming",
      facts: [
        "Weekly coding tutorials and project breakdowns",
        "Exclusive access to source code and resources",
        "Live Q&A sessions every Friday"
      ],
      subscribers: 15420,
      monthlyPrice: 9.99
    },
    {
      id: 2,
      name: "Michael Chen",
      photo: "/images/creator-profile-2.jpg",
      specialty: "Digital Marketing",
      facts: [
        "Advanced SEO strategies and case studies",
        "Social media growth techniques",
        "Monthly strategy workshops"
      ],
      subscribers: 12850,
      monthlyPrice: 12.99
    },
    {
      id: 3,
      name: "Emily Rodriguez",
      photo: "/images/creator-profile-3.jpg",
      specialty: "Fitness & Wellness",
      facts: [
        "Personalized workout plans and nutrition guides",
        "Live fitness classes and challenges",
        "Exclusive meal prep recipes"
      ],
      subscribers: 9870,
      monthlyPrice: 7.99
    },
    {
      id: 4,
      name: "James Wilson",
      photo: "/images/creator-profile-4.jpg",
      specialty: "Photography & Design",
      facts: [
        "Advanced editing tutorials and presets",
        "Equipment reviews and recommendations",
        "Monthly photo challenges and critiques"
      ],
      subscribers: 11230,
      monthlyPrice: 14.99
    },
    {
      id: 5,
      name: "Lisa Thompson",
      photo: "/images/creator-profile-5.jpg",
      specialty: "Business & Entrepreneurship",
      facts: [
        "Startup strategies and funding insights",
        "Business model analysis and case studies",
        "Networking events and mentorship"
      ],
      subscribers: 14560,
      monthlyPrice: 19.99
    },
    {
      id: 6,
      name: "Robert Kim",
      photo: "/images/creator-profile-6.jpg",
      specialty: "Gaming & Entertainment",
      facts: [
        "Exclusive gaming content and strategies",
        "Behind-the-scenes streaming insights",
        "Community events and tournaments"
      ],
      subscribers: 8760,
      monthlyPrice: 5.99
    }
  ];

  const handleSubscribe = (creatorId) => {
    if (subscribedCreators.includes(creatorId)) {
      setSubscribedCreators(subscribedCreators.filter(id => id !== creatorId));
    } else {
      setSubscribedCreators([...subscribedCreators, creatorId]);
    }
  };

  return (
    <div className="facts-page">
      <Navbar isDarkMode={isDarkMode} toggleTheme={toggleTheme} />
      
      <div className="facts-header">
        <h1>Premium Content Creators</h1>
        <p>Discover amazing creators and unlock exclusive content with monthly subscriptions</p>
      </div>

      <div className="creators-container">
        <h2>Featured Creators</h2>
        <div className="creators-grid">
          {creators.map((creator) => (
            <div key={creator.id} className="creator-card">
              <div className="creator-photo">
                <img 
                  src={creator.photo} 
                  alt={creator.name}
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src = "https://images.unsplash.com/photo-1494790108755-2616b612b786?w=400&h=400&fit=crop&crop=face";
                    console.warn(`Failed to load image for ${creator.name}, using fallback`);
                  }}
                />
              </div>
              <div className="creator-info">
                <h3>{creator.name}</h3>
                <p className="specialty">{creator.specialty}</p>
                <p className="subscribers">{creator.subscribers.toLocaleString()} subscribers</p>
                <p className="price">${creator.monthlyPrice}/month</p>
                
                <div className="facts-list">
                  <h4>What you'll get:</h4>
                  <ul>
                    {creator.facts.map((fact, index) => (
                      <li key={index}>{fact}</li>
                    ))}
                  </ul>
                </div>

                <button 
                  className={`subscribe-btn ${subscribedCreators.includes(creator.id) ? 'subscribed' : ''}`}
                  onClick={() => handleSubscribe(creator.id)}
                >
                  {subscribedCreators.includes(creator.id) ? 'Subscribed âœ“' : `Subscribe $${creator.monthlyPrice}/mo`}
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>

      <div className="facts-summary">
        <h2>Why Choose SubHub?</h2>
        <div className="summary-grid">
          <div className="summary-item">
            <h3>ðŸŽ¯ Premium Content</h3>
            <p>Access exclusive content that's not available anywhere else</p>
          </div>
          <div className="summary-item">
            <h3>ðŸ‘¥ Community Access</h3>
            <p>Join exclusive communities and connect with like-minded people</p>
          </div>
          <div className="summary-item">
            <h3>ðŸ“± Mobile Friendly</h3>
            <p>Access your subscriptions on any device, anywhere</p>
          </div>
          <div className="summary-item">
            <h3>ðŸ’³ Secure Payments</h3>
            <p>Safe and secure payment processing with instant access</p>
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default FactsPage;
