import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Navbar from './Navbar';
import Footer from './Footer';
import './Homepage.css';
import axios from 'axios';

const Homepage = ({ isDarkMode, toggleTheme }) => {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchArticles = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/article/all', {
          headers: {
            "Authorization": `Bearer ${localStorage.getItem("token")}`,
          },
        });
        console.log('Fetched articles:', response.data);
        setArticles(response.data.slice(0, 3));
      } catch (error) {
        console.error('Error fetching articles:', error);
        setArticles([]);
      } finally {
        setLoading(false);
      }
    };

    fetchArticles();
  }, []);

  const getImageUrl = (imageUrl) => {
    if (!imageUrl || imageUrl === 'undefined' || imageUrl === null) {
      console.warn('Invalid imageUrl field, using default:', imageUrl);
      return '/images/default-article.jpg';
    }
    return `http://localhost:8080/api/article/image/${imageUrl}`;
  };

  return (
    <div className="home-page">
      <Navbar isDarkMode={isDarkMode} toggleTheme={toggleTheme} />

      <div className="hero" style={{ marginTop: '60px' }}>
        <div className="text-content">
          <h1>Unlock Premium</h1>
          <h2>Content Today</h2>
          <p>Join thousands of creators and subscribers on the ultimate content platform</p>
        </div>
        <img src="/images/creators-hero.png" alt="Content Creators" />
      </div>

      <div className="services">
        <div className="service-title1">Platform Features</div>
        <div className="services-container">
          <div className="service-card">
            <img src="/images/creator-icon.png" alt="Creator" />
            <h3>Become a Creator</h3>
            <p>Start monetizing your content and build a loyal subscriber base with our creator tools.</p>
          </div>
          <div className="service-card">
            <img src="/images/subscription-icon.png" alt="Subscription" />
            <h3>Premium Subscriptions</h3>
            <p>Access exclusive content from your favorite creators with flexible subscription plans.</p>
          </div>
          <div className="service-card">
            <img src="/images/community-icon.png" alt="Community" />
            <h3>Join Communities</h3>
            <p>Connect with like-minded people in exclusive creator communities and discussions.</p>
          </div>
          <div className="service-card">
            <img src="/images/analytics-icon.png" alt="Analytics" />
            <h3>Creator Analytics</h3>
            <p>Track your growth and earnings with detailed analytics and insights dashboard.</p>
          </div>
        </div>
      </div>

      <div className="recent-articles">
        <div className="articles-header" style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', width: '100%', maxWidth: '1200px', paddingBottom: "30px" }}>
          <h2 style={{ margin: 'auto', paddingLeft: "250px" }}>Trending Creators</h2>
          <Link to="/facts" className="see-all" style={{ marginLeft: 'auto', paddingTop: "20px" }}>See All →</Link>
        </div>

        <div className="article-container2">
          {loading ? (
            <p>Loading creators...</p>
          ) : articles.length > 0 ? (
            articles.map((article, index) => (
              <div className="article-card" key={article.id}>
                <img
                  src={getImageUrl(article.imageUrl)}
                  alt={article.title}
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src = '/images/default-article.jpg';
                    console.warn(`Failed to load image for article ${article.title}, using default`);
                  }}
                />
                <h3>{article.title}</h3>
                <p>
                  {article.content && article.content.length > 100
                    ? `${article.content.substring(0, 100)}...`
                    : article.content || 'No content available'}
                </p>
              </div>
            ))
          ) : (
            <p>No trending creators available.</p>
          )}
        </div>
      </div>

      <div className="faq-section">
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', width: '100%', maxWidth: '1200px', paddingBottom: "30px" }}>
          <h2 style={{ margin: 'auto', paddingLeft: "250px" }}>Frequently Asked Questions</h2>
          <Link to="/faq" className="see-all" style={{ marginLeft: 'auto', paddingTop: "20px" }}>See All →</Link>
        </div>
        <div className="faq-container">
          <div className="faq-image">
            <img src="/images/faq-icon.png" alt="FAQ Icon" />
          </div>

          <div className="faq-questions">
            {[
              { question: "How do I become a creator on SubHub?", answer: "Sign up for a creator account, set up your profile, and start creating content. You can monetize through subscriptions, tips, and exclusive content." },
              { question: "What payment methods do you accept?", answer: "We accept all major credit cards, PayPal, and digital wallets. Payments are processed securely and you can cancel anytime." },
              { question: "Can I cancel my subscription anytime?", answer: "Yes, you can cancel your subscription at any time. You'll continue to have access until the end of your current billing period." },
              { question: "How much can creators earn?", answer: "Creator earnings depend on subscriber count and subscription price. Top creators can earn $5,000+ monthly with our platform." },
            ].map((faq, index) => (
              <div className="faq-item" key={index}>
                <div className="faq-question">{faq.question}<span className="toggle-icon">+</span></div>
                <div className="faq-answer hidden">{faq.answer}</div>
              </div>
            ))}
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default Homepage;
